import { UPDATE_COLLECTIONS } from "../types";

const INTIAL_STATE = {
  collections: null,
  isFetching: false,
  fetchSuccess: false,
  fetchFailed: false
};

const shopReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case UPDATE_COLLECTIONS:
      return {
        ...state,
        collections: action.payload
      };
    default:
      return state;
  }
};

export default shopReducer;
