import SHOP_DATA from "../../Pages/Shop/Shop.data";
import { UPDATE_COLLECTIONS } from "../types";

const INTIAL_STATE = {
  collections: SHOP_DATA
};

const shopReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case UPDATE_COLLECTIONS:
      return null;
    default:
      return state;
  }
};

export default shopReducer;
