// run multiple sagas
import { all, call } from "redux-saga/effects";
import { fetchCollectionsStart } from "./shopSagas";
import { userSagas } from "./userSagas";

export default function* rootSagas() {
  yield all([call(fetchCollectionsStart), call(userSagas)]);
}
